pragma solidity >=0.5.0 <0.9.0;

contract Tickets{
    address public purchasers;
    // uint256 constant Total_Supply = 20;
    event Product(string product_name, uint quantity, string url, uint price);

    struct ProductDetails {
        uint quantity;
        address[] purchasers;
        string url;
        uint price;
        bool isSet;
    }
    mapping(string => ProductDetails) public productList;

    struct Ticket{
        address owner;
        uint256 price;
    }

    // Ticket[Total_Supply] public tickets;

    constructor() public{
        // for (uint256 i = 0;i < Total_Supply; i++){
        //     tickets[i].owner = address(0x0);
        //     tickets[i].price = 1e17;
        // }
        purchasers = msg.sender;
    }

    function addProduct(string memory product_name, uint addQuantity, string memory url, uint price) public {
        productList[product_name].quantity = addQuantity;
        productList[product_name].url = url;
        productList[product_name].price = price;
        productList[product_name].isSet = true;

        emit Product(product_name, addQuantity, url, price);
    }

    function addProductQuantity(string memory product, uint addQuantity) public {
        require(productList[product].isSet);
        productList[product].quantity += addQuantity;
    }

    function purchaseProduct(string memory product_name, address purchasers) external payable {
        require(productList[product_name].purchasers.length < productList[product_name].quantity); 
        productList[product_name].purchasers.push(purchasers);
    }

    function getPurchasers(string memory product) public view returns (address[] memory purchasers) {
        return productList[product].purchasers;
    }

    // function buyTicket(uint256 _index) external payable{
    //     require(_index < Total_Supply && _index >= 0);
    //     require(tickets[_index].owner == address(0x0));
    //     require(msg.value >= tickets[_index].price);

    //     tickets[_index].owner = msg.sender;
    // }

}
